import mongoose from 'mongoose';

const userModel = new mongoose.Schema({
    username:{
        type:String,
        required:true,
        unique:true,
    },
    email:{
        type:String,
        required:true,
        unique:true
    },
    password:{
        type:String,
        required:true,
    },
    profilePicture:{
        type:String,
        default:"data:image/jpeg;base64,"
    }

},{timestamps:true});

const User = mongoose.model("User",userModel);
export default User;
